import React, {Component} from 'react';
import axios from 'axios';
import { BrowserRouter as Router, Route, Link ,withRouter} from "react-router-dom";
import './style.css'

export default  class Addproduct extends Component{
    constructor(props){
        super(props)
        console.log(props)
        this.state={
            id:this.props.match.params.id,
            name:'',
            price:'',
            quantity:''
        }
        this.onChangename = this.onChangename.bind(this);
        this.onChangeprice = this.onChangeprice.bind(this);
        this.onChangequantity = this.onChangequantity.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
    }
    onChangename(e){
        this.setState({name:e.target.value})
    }
    onChangeprice(e){
        this.setState({price:e.target.value})
    }
    onChangequantity(e){
        this.setState({quantity:e.target.value})
    }
    onSubmit(e){
        e.preventDefault();
        const newData={
            id:this.state.id,
            name: this.state.name,
            price: this.state.price,
            quantity: this.state.quantity
        }
        console.log(this.props.match.params.id)

       console.log(newData)
       axios.post('http://localhost:4000/list/add', newData)
        .then(res=>console.log(res));

    }
    render(){
        return(
        <div>
            <div id="main-registration-container">
            <div id="register">
               <h3>Add Product</h3>
               <form   name="userRegistrationForm"  onSubmit= {this.onSubmit} >


               <label>Name of Product</label>
               <input type="text" name="username" value={this.state.fields.username} onChange={this.onChangeUsername} />
               <div className="errorMsg">{this.state.errors.username}</div>


               <label>Price</label>
               <input type="number" name="price" value={this.state.fields.password} onChange={this.onChangePassword} />
               <div className="errorMsg">{this.state.errors.password}</div>


               <label>Quantity</label>
               <input type="number" name="password" value={this.state.fields.password} onChange={this.onChangePassword} />
               <div className="errorMsg">{this.state.errors.password}</div>


           <br/>
               <input type="submit" className="button"  value="Login"/>
               </form>
            </div>
            </div>
        </div>
        )
    }
}
