import 'bootstrap/dist/css/bootstrap.min.css'
import { Navbar, Nav, NavItem, NavDropdown, MenuItem ,Card,Button} from 'react-bootstrap';
import React, {Component} from 'react';
import { BrowserRouter as Router, Route, Link,Redirect } from "react-router-dom"
import axios from 'axios';

export default class vendor extends Component{
    constructor(props)
    {
        super(props)
        this.state={
            id:this.props.match.params.id,
            arr:{},
            product:{},
            len:0
        }
        axios.get('http://localhost:4000/order/'+this.state.id)
        .then(res=>{
            this.setState({arr:res.data})
            this.setState({len:res.data.length})
            console.log(this.state.arr)
        })
    }

    render()
    {   
       let array=[]
            for(var i=0;i<this.state.len;i++){
            array.push(
                <div>
                <Card>
                <Card.Header>Order {i+1}</Card.Header>
                <Card.Body>
                    <Card.Title>Special title treatment</Card.Title>
                    <Card.Text>
                    With supporting text below as a natural lead-in to additional content.
                    </Card.Text>
                    <Button variant="primary">Go somewhere</Button>
                </Card.Body>
                </Card>
                </div>
            )
        }

        return(
            <div>
            {array}
            </div>
        )
    }
}
