import React, {Component} from 'react';
import axios from 'axios';
import homepage from './home';
import { BrowserRouter as Router, Route, Link,Redirect } from "react-router-dom"
import Register from './register';
import 'bootstrap/dist/css/bootstrap.min.css'
import { Navbar, Nav, NavItem, NavDropdown, MenuItem } from 'react-bootstrap';
import './style.css'
export default class Login extends Component{
    constructor(props) {
        super(props);

        this.state = {
            fields: {},
            errors: {},
            username: '',
            password: '',
            type: 'vendor',
            redirect: false,
            id: ' '
        }

        this.onChangeUsername = this.onChangeUsername.bind(this);
        this.onChangePassword = this.onChangePassword.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
        this.onChangeType=this.onChangeType.bind(this);
    }
    onChangeUsername(e) {
        let fields = this.state.fields;
        fields[e.target.name] = e.target.value;
        this.setState({
          fields
        });
        this.setState({ username: e.target.value });
    }

    onChangePassword(e) {
        let fields = this.state.fields;
        fields[e.target.name] = e.target.value;
        this.setState({
          fields
        });
        this.setState({ password: e.target.value });
    }
    onChangeType(e){
        this.setState({type:e.target.value});
    }

    onSubmit(e) {
        const check=0
        e.preventDefault();
        if (this.validateForm()) {
            console.log(this.state)
            let fields = {};
            fields["username"] = "";
            // fields["emailid"] = "";
            // fields["mobileno"] = "";
            fields["password"] = "";
            this.setState({fields:fields});
            alert("Form submitted");
            const newUser = {
                username: this.state.username,
                password: this.state.password
            }
            if(this.state.type=='customer'){
                axios.post('http://localhost:4000/buyer/add', newUser)
                    .then(res => console.log(res));
            }
            else {
                axios.post('http://localhost:4000/vendor/add', newUser)
                .then(res => console.log(res));
            }
            this.setState({
                username: '',
                password: '',
                type: ''
            });
        }

    }
    render(){
        if(this.state.redirect)
        {
          return <Redirect push to={"vendor/"+this.state.id}/>
        }
        return(
            <div>

            <Navbar collapseOnSelect expand="lg" bg="dark" variant="dark">

            <Navbar.Toggle aria-controls="responsive-navbar-nav" />
            <Navbar.Collapse id="responsive-navbar-nav">
              <Nav className="mr-auto">
                <Nav.Link href="/">App</Nav.Link>
                <Nav.Link href="/register">Register</Nav.Link>
                <Nav.Link href="/login">Login</Nav.Link>
             </Nav>
             </Navbar.Collapse>
             </Navbar>
            <h1 >Login </h1>

            <form onSubmit={this.onSubmit}>
            <div >
                <label>Username : </label>
                <input type="text" 

                    value={this.state.username}
                    onChange={this.onChangeUsername}
                    />
            </div>
            <div >
                <label>Password : </label>
                <input type="text" 

                    value={this.state.password}
                    onChange={this.onChangePassword}
                    />  
            </div>
            <div>
                <select value={this.state.type} onChange={this.onChangeType}>
                    <option value="vendor">Vendor</option>
                    <option value="customer">Customer</option>

                </select>
            </div>
            <div >
                <input type="submit" value="Login" />
            </div>
        </form>
            </div>
            )
    }
}
